package contest.top100CN;

import org.junit.jupiter.api.Test;

import java.util.LinkedList;
import java.util.List;

/**
 * https://leetcode-cn.com/problems/find-all-anagrams-in-a-string/
 *
 * @author Ikarosx
 * @date 2020/7/30 7:47
 */
public class N438找到字符串中所有字母异位词 {
    @Test
    public void test() {
        System.out.println(findAnagrams("", ""));
    }
    
    public List<Integer> findAnagrams(String s, String p) {
        // p保证非空
        LinkedList<Integer> result = new LinkedList<>();
        int sl = s.length();
        int pl = p.length();
        if (sl < pl || p.equals("")) {
            return result;
        }
        // 记录s和p中相同的字母数
        int count = 0;
        int[] schars = new int[256];
        int[] pchars = new int[256];
        // 记录p中的字母
        for (int i = 0; i < pl; i++) {
            pchars[p.charAt(i)]++;
        }
        // 记录s中前pl个字母
        for (int i = 0; i < pl; i++) {
            char sc = s.charAt(i);
            int pchar = pchars[sc];
            if (++schars[sc] == pchar) {
                count += pchar;
            }
        }
        if (count == pl) {
            result.add(0);
        }
        for (int i = 1; i < sl - pl + 1; i++) {
            // 先减掉开头的
            char sc = s.charAt(i - 1);
            int pchar = pchars[sc];
            if (--schars[sc] == pchar - 1) {
                count -= pchar;
            }
            // 加上结尾的
            char c = s.charAt(i + pl - 1);
            int pchar1 = pchars[c];
            if (++schars[c] == pchar1) {
                count += pchar1;
            }
            if (count == pl) {
                result.add(i);
            }
        }
        return result;
    }
}
